(function($) {
    "use strict"; // Start of use strict

    // Smooth scrolling using jQuery easing
    $('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function() {
        if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
            var target = $(this.hash);
            target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
            if (target.length) {
                $('html, body').animate({
                    scrollTop: (target.offset().top - 48)
                }, 1000, "easeInOutExpo");
                return false;
            }
        }
    });

    // Closes responsive menu when a scroll trigger link is clicked
    $('.js-scroll-trigger').click(function() {
        $('.navbar-collapse').collapse('hide');
    });

    // Activate scrollspy to add active class to navbar items on scroll
    $('body').scrollspy({
        target: '#mainNav',
        offset: 54
    });

    // Collapse Navbar
    var navbarCollapse = function() {
        if ($("#mainNav").offset().top > 100) {
            $("#mainNav").addClass("navbar-shrink");
        } else {
            $("#mainNav").removeClass("navbar-shrink");
        }
    };
    // Collapse now if page is not at top
    navbarCollapse();
    // Collapse the navbar when page is scrolled
    $(window).scroll(navbarCollapse);

})(jQuery); // End of use strict

function showCourierContent() {
    var courierContent = document.getElementById("courier-content");
    var forwardingContent = document.getElementById("forwarding-content");
    var roadTransportContent = document.getElementById("road-transport-content");

    if (forwardingContent.classList.contains("service-visible")) {
        forwardingContent.classList.remove("service-visible");
        forwardingContent.classList.add("service-invisible");
    }

    if (roadTransportContent.classList.contains("service-visible")) {
        roadTransportContent.classList.remove("service-visible");
        roadTransportContent.classList.add("service-invisible");
    }

    if (courierContent.classList.contains("service-invisible")) {
        courierContent.classList.remove("service-invisible");
        courierContent.classList.add("service-visible");
    }
};

